import React from 'react';
import {Form, Input, Button} from 'antd'

const dummy = {
  isLoggedIn:true,
  imagePaths:[],
  mainpost:[]
};

const Home = () =>{
    return (
        <div>
            {dummy.isLoggedIn && <Form encType = "multipart/form-data">
            <Input.TextArea maxLength = {150} placecholder = "어떤 일이 있었나요"/>
            <div>
                <Input type = "file" multiple hidden></Input>
                <Button>이미지 업로드</Button>
                <Button type = "primary" style = {{float:"right"}} htmlType = "submit">제출</Button>
            </div>
            <div>
                {dummy.imagePaths.map((v,i)=>{
                    return(
                        <div key={v} style={{display:'inline-block'}}>
                            <img src={'http://localhost:3065/'+v} style = {{width:'200px'}} alt = {v}/>
                            <div>
                                <Button>제거</Button>
                            </div>
                        </div>
                    );
                })}
            </div>
            </Form>}
            {dummy.mainpost.map((c)=>{
                
            })}
        </div>
    );
};

export default Home;
